mapper.select:
update edu_list 
set edu_type=#{edu_type}, edu_name=#{edu_name}, edu_teacher=#{edu_teacher}, edu_place=#{edu_place}, 
edu_content=#{edu_content}, edu_start_date=#{edu_start_date}, edu_end_date=#{edu_end_date}, 
edu_apply_end=#{edu_apply_end}, edu_thumbnail_src=#{edu_thumbnail_src}, edu_personnel=#{edu_personnel} 
where edu_id = #{edu_id}

update his_edu 
set edu_status = '교육취소' 
where edu_his_id = #{edu_his_id}

select edu_his_id emp_id, emp_name, edu_name, edu_apply_end, edu_start_date, edu_end_date, edu_status 
from his_edu h, edu_list l, employee e 
where h.edu_id = l.edu_id and h.emp_id = e.emp_id and edu_name like #{edu_name}

select edu_his_id, h.emp_id, emp_name, edu_name, edu_apply_end, edu_start_date, edu_end_date, edu_status 
from his_edu h, edu_list l, employee e 
where h.edu_id = l.edu_id and h.emp_id = e.emp_id and h.emp_id = #{emp_id}

select edu_his_id, h.emp_id, emp_name, edu_name, edu_apply_end, edu_start_date, edu_end_date, edu_status 
from his_edu h, edu_list l, employee e 
where h.edu_id = l.edu_id and h.emp_id = e.emp_id and emp_name = #{emp_name}
